import React from "react";
import Cal from "./cal";
import "./App.css"

class App extends React.Component {
  state = {
    inputStr: ""
  }

  inputc(inputChar){
    console.log("input: "+inputChar);
    this.setState({inputStr: inputStr + inputChar});
  }

  render() {
    const {inputStr} = this.state;
    return(
      <div id="calculator">
        <div id="display">
          <div id="answer">
            {inputStr}
          </div>
        </div>
        <div id="calculatorBody">
          <div className="buttonWrap">
            <button onClick={this.inputc('CE')}>CE</button>
            <button onClick={this.inputc('(')}>(</button>
            <button onClick={this.inputc(')')}>)</button>
            <button onClick={this.inputc('X')}>X</button>
          </div>
          <div className="buttonWrap">
            <button onClick={this.inputc('7')}>7</button>
            <button onClick={this.inputc('8')}>8</button>
            <button onClick={this.inputc('9')}>9</button>
            <button onClick={this.inputc('/')}>/</button>
          </div>
          <div className="buttonWrap">
            <button onClick={this.inputc('4')}>4</button>
            <button onClick={this.inputc('5')}>5</button>
            <button onClick={this.inputc('6')}>6</button>
            <button onClick={this.inputc('-')}>-</button>
          </div>
          <div className="buttonWrap">
            <button onClick={this.inputc('1')}>1</button>
            <button onClick={this.inputc('2')}>2</button>
            <button onClick={this.inputc('3')}>3</button>
            <button onClick={this.inputc('+')}>+</button>
          </div>
          <div className="buttonWrap">
            <button onClick={this.inputc('.')}>.</button>
            <button onClick={this.inputc('0')}>0</button>
            <button onClick={this.inputc('del')}>del</button>
            <button onClick={this.inputc('=')}>=</button>
          </div>
        </div>
      </div>
    )
  };
}

export default App;
